SELECT SUM(c.Amount) AS 'Amount' FROM Checks AS c INNER JOIN CheckRun AS cr ON cr.CheckRunID = c.CheckRunID INNER JOIN Creditors AS cred ON c.CreditorID = cred.CreditorID INNER JOIN ClientCred AS clcr ON clcr.CreditorID = cred.CreditorID INNER JOIN Clients AS cl ON cl.ClientID = clcr.ClientID WHERE cl.ClientID = 13879 AND clcr.AccountNumber = 'CLIENT REFUND' AND cr.CheckRunDate >= dbo.StartofDay(GETDATE()) AND cr.CheckRunDate <= dbo.StartofNextDay(DATEADD(day,1,GETDATE())) GROUP BY cl.ClientID ORDER BY cl.ClientID